<?php $URL=path("blog/". $post["Year"] ."/". $post["Month"] ."/". $post["Day"] ."/". $post["Slug"]);$in=($post["Tags"] !=="") ? __("in") : NULL;?><div class="post"><div class="post-title"><a href="<?php echo $URL;?>" title="<?php echo stripslashes($post["Title"]);?>"><?php echo stripslashes($post["Title"]);?></a></div><div class="post-left"><?php echo __("Published") ." ". howLong($post["Start_Date"]) ." $in ". exploding($post["Tags"], "blog/tag/") ." " . __("by") . ' <a href="'. path("blog/author/". $post["Author"]) .'">'. $post["Author"] .'</a>';?></div><div class="post-right"><?php if($post["Enable_Comments"]){echo fbComments($URL, TRUE);}?></div><div class="clear"></div><div class="post-content"><?php echo social($URL, $post["Title"], FALSE);echo showContent($post["Content"], $URL);?><br/><br/><?php echo display('<p>'. getAd("728px") .'</p>', 4);?></div></div><br/></br/><?php if($post["Enable_Comments"]){echo fbComments($URL);}?>